css:
@-moz-document url-prefix("https://tweetdeck.twitter.com/"), url-prefix("https://web.tweetdeck.com/")

{
.tweet-text {font-size: 18px; font-family: 'Hacen Liner Print out Light',Tahoma, arial;}

.is-wide-columns .column {width: 585px;}

.column {margin: 0 6px 0 0;}

.is-wide-columns .media-size-medium {height: 300px;}

@font-face {
    font-family: 'Hacen Liner Print out Light';
    src: url('http://iphoneislam.com/wp-content/themes/remal/fonts/Hacen_Liner_Print_out_Light.eot');
    src: url('http://iphoneislam.com/wp-content/themes/remal/fonts/Hacen_Liner_Print_out_Light.eot?#iefix') format('embedded-opentype'),
         url('http://iphoneislam.com/wp-content/themes/remal/fonts/Hacen_Liner_Print_out_Light.woff') format('woff'),
         url('http://iphoneislam.com/wp-content/themes/remal/fonts/Hacen_Liner_Print_out_Light.ttf') format('truetype');
    font-weight: normal;
    font-style: normal;}
}
