css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("lighthouseapp.com") {
  #header {
    height: 72px !important;
  }

  #rheader {
    padding-top: 3px !important;
  }

  #action-nav {
    height: 24px !important;
  }
  
  #action-nav ul {
    padding: 0 !important;
  }

  #ticket-body {
    height: 100px;
  }
  
  #attachments-grid {
    margin-top: 3px !important;
    border: 0 !important;
    padding-bottom: 0 !important;
  }
  
  #attachments-grid dt {
    margin-top: 0 !important;
  }

  .btns {
    margin: 0 !important;
  }

  .greet {
    padding: 5px 9px 0 9px !important;
  }  

  .changes {
    margin-top: 0 !important;
  }

  .list-form li {
    padding: 0px !important;
  }

  #taggings {
    position: absolute;
    top: 132px;
    right: -5px;
    width: 241px;
  }

  #taggings input {
    margin: 0 !important;
  }
  
  .group-main dt {
    margin-top: 0 !important;
  }
  
  #ticket ul.info {
    margin: 0 !important;
  }
}
