@-moz-document domain(tumblr.com) {

#posts.posts {
    list-style-type: none;
    margin: 0 0 0 9px;
    padding: 0;
}
.photoset_row_2 img{  width: 150px !important;  }
.photoset_row_1 {height: 150px !important; } 
.left_column { width: 100% !important;   }
.post_container { width: 300px !important;  float:left !important;  padding: 5px; height: 500px; overflow:auto;  overflow-x: hidden;}
.photoset_photo img  { width: 300px !important;  height: 100% !important; float:left; }

.post_wrapper{  width: 300px !important;   float:left !important; }
.post_full.is_photo .post_media .post_media_photo, .post_full.is_photo .post_media .post_media_photo_anchor .photoset_row .photoset_row_1 .post_media_small img  { width: 300px !important;  height: 100% !important; float:left!important; }
.standalone-ad-container { display: none;  }
.notification, .single_notification  {  display: none;   }
.post_container .sponsored_post  {  display: none;  width: 1% !important;   }
.embed_media_embed_video_post, .post_media, ._q3gpi, .EmbedFrame ,.EmbedMedia{ width: 300px !important;  }

/* Scrollbar styles */
::-webkit-scrollbar {
width: 5px;
height: 5px;
}

::-webkit-scrollbar-track {
box-shadow: inset 0 0 10px gray;
border-radius: 5px;
}

::-webkit-scrollbar-thumb {
border-radius: 10px;
background: #fffaf0; 
box-shadow: inset 0 0 6px rgba(0,0,0,0.5); 
}

.image-ad {
    float: left;
    width: 300px;
}

.l-container {
    padding: 0px;
    padding-top: 92px;
}

.left_column {
    width: 100%;
}

.photoset_row {
    margin: 0;
    padding: 0;
    width: 300px;
}

.post.post_full {
    margin: 0;
    padding: 0;
    width: 300px;
}

.post_body {
    display: none;
}

.post_container {
    float: left;
    width: 310px;
}

.post_full.is_photo .post_media .post_media_photo, .post_full.is_photo .post_media .post_media_photo_anchor .photoset_row_1 .photoset_row {
    height: 100%;
    width: 100%;
}

.post_full.is_photoset .photoset .photoset_row {
    height: 120px;
    width: 300px;
}

.post_full.is_photoset .photoset .photoset_row .photoset_photo img {
    height: 100%;
    width: 300px;
}

.post_full.is_photoset .photoset .photoset_row, .post_full.is_photo .photoset .photoset_row {
    margin: 0;
    padding: 0;
    width: 300px;
}

.reblog-list, .post-source-footer , .reblog-list-item.contributed-content {
    display: none;
    padding: 10px;
}

.reblog-list.hover, .post-source-footer.hover , .reblog-list-item.contributed-content.hover {
    display: none;
    padding: 10px;
}

.standalone-ad-container {
    float: left;
    margin: 10px;
    width: 300px;
}

a.post_avatar_link {
    display: none;
}

div.banner_notification_inner.stretchy_kid.clearfix {
    display: none;
}

div.controls_section.follow_list.user_list.is_rendering.recommended_tumblelogs {
    display: none;
}

div.l-container.l-container--two-column-dashboard {
    width: 100%;
}

div.l-content.clearfix {
    width: 100%;
}



div.left_column {
    width: 100%;
}

div.new_post_buttons.post.post_full.is_mine.with_avatar.new_post {
    display: none;
}

div.pinned_sidebar_footer {
    display: none;
}

div.post_avatar.show_user_menu {
    display: none;
}

div.post_avatar_wrapper {
    display: none;
}

div.post_control.post_control_menu.sponsor {
    display: none;
}

div.right_column {
    display: none;
}

i.icon.icon_arrow_carrot_right {
    display: none;
}

left_column {
    width: 100%;
}

li.post_container.new_post_buttons_container {
    display: none;
}

nav.post_buttons.clearfix {
    display: none;
}

standalone-ad-container .l-container {
    margin: 0;
    padding: 0;
    width: 300px;
}
}
