css:
@-moz-document domain("www.flashback.org") {
/* improve readability */
body { font-size: 1.3rem; }
_#site .post_message { font-size: 1.3rem; }

/* force UI-font */
body, .post_message {
    font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol" !important;
    font-size: 1.4rem !important;
}

#site .page-title a {
    font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol" !important;
}

#navbar-forumnav .dropdown a {
	font-size: 1.3rem;
}

body, #site .page-title a, html > body, .post_message, #site td, #site caption {
    font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol" !important;
}

.post .post-body .post-right {
    font-family:'Open Sans',sans-serif;font-family:Verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif;
    font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol" !important;
    font-size: 1.4rem;
}


#site .thread-title, #site .td_title a { font-size: 1.4rem !important; }
#site .forumslist strong { font-size: 1.4rem !important; }
}
