css:
@-moz-document domain('dietapaleo.org') {

.entry-content {
    font-size: 18px;
    font-weight: 400;
    line-height: 1.555;
    /* 28px */
}

.entry-content ol,
.entry-content p,
.entry-content ul,
.quote-caption,
.clear-line {
    margin-top: 0;
    margin-bottom: 28px;
}

.entry-content h4 {
    font-size: 22px;
}

div.easyrecipe {
    font: inherit;
    border: 1px dashed #666;
    color: #666;
    padding: 10px;
    background: #FFF;
}

.foodie-pro .easyrecipe .ERSName {
    width: calc(70% - 28px);
}

.easyrecipe .ERSTimes .ERSTimeItem {
    font-size: inherit;
    font-weight: normal;
}

.easyrecipe .ERSTimes .ERSTime {
    width: 33%;
    text-align: center;
    float: left;
    font-weight: bold;
    padding: 7px;
}

.easyrecipe .ERSTimes {
    border-top: 1px dotted #666;
    border-bottom: 1px dotted #666;
    width: calc(70% - 28px);
    padding-top: 5px;
    padding-bottom: 5px;
    margin-bottom: 10px;
}

.foodie-pro .easyrecipe .ERSIngredients {
    width: 100%;
}
}
