css:
/* Adblocker */ 
@-moz-document domain('chan.sankakucomplex.com'), url(https://www.fgsadcvbnuycgf.com/)
{
  .scad,
  .scad-i,
  .sidebar iframe {
    display: none !important;
  }
  div#sp1{display:none!important;}
  div.response-list > div.comment > div.content .body.bad{color: #000 !important;}
}  

/* No Blacklist on search results pages only */
@-moz-document url-prefix("https://chan.sankakucomplex.com/?tags"), url(https://www.fgsadcvbnuycgf.com/) { 
  span.blacklisted {display:none !important;}
  span.thumb{width: auto;}
}

/* No recommended on post pages only */
@-moz-document url-prefix("https://chan.sankakucomplex.com/post/show"), url(https://www.fgsadcvbnuycgf.com/) { 
  div#recommended{display:none!important;} 
}
