css:
@-moz-document url-prefix("https://leipzig.on-test.de/projects/local-marketing-tool/issues/gantt")
{

.gantt_subjects div,
.controller-gantts #main #content table tr td:first-child,
.controller-gantts #main #content table tr td:first-child > div .gantt_hdr
{
width: 500px!important;
}

.controller-gantts #main #content table tr td:first-child > div
{
width: 501px!important;
}



#gantt_area.fixhead .gantt_hdr 
{
margin-left: 513px;
position: fixed;
z-index: 2;
}

#gantt_area.fixhead .gantt_hdr:first-child 
{
margin-left: 0px;
}

}
