css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("whitehouse2.org") {
  .endorsement_up   {background: lightgreen !important;}
  .endorsement_down {background: salmon !important;}

  div.pagination {margin-bottom: 5px !important;}

  div.priority_listing {padding: 9px 9px 6px 9px !important; border: solid 1px transparent !important;}
  div.priority_listing:hover {background: #D5E5EB!important; border: solid 1px #ACB4BA !important;}

  div.home_issue_priority {padding: 3px 3px 2px 3px !important; border: solid 1px transparent !important;}
  div.home_issue_priority:hover {background: #D5E5EB!important; border: solid 1px #ACB4BA !important;}

  div.point {padding: 3px 3px 2px 3px !important; border: solid 1px transparent !important;}
  div.point:hover {background: #D5E5EB!important; border: solid 1px #ACB4BA !important;}

  div.priority_listing a.priority_name_link {color: #13499B !important;}

  table#infocus + table#infocus {display: none;}

  form#new_comment textarea {height: 250px !important;}
}
