css:
@-moz-document domain(music.yandex.ru) {

body,
.track__cover, .artist__cover, .album-cover, .concert__cover, .playlist-cover-stack_decorated, .artist-pics__pic, .promo, .video,
.nb-user-avatar, .head__userpic,
.station-icon, .station__title, .background,
.teaser,
.page-genre__head,
.nav__banner-content, .footer__static-text,
.button-store_all
{
    filter: invert(1);
}

.logo, .button, .slider-vert, .button2, .player-controls__btn, .progress, .nav__tab, .tabs__tab, .sidebar-album {
    filter: hue-rotate(180deg) saturate(3.7);
}

.icon_money {
    filter: invert(1) brightness(0.5) saturate(5);
}

}
