css:
@-moz-document domain('mangainn.net'),
url(http://www.mangainn.net/) {
#imgPage {
    height: auto;
    left: initial;
    margin: auto;
    max-width: 100%;
    min-width: 60rem;
    position: initial;
    width: 60%;
}

#shareme {
    display: block;
    left: 0;
    position: absolute;
    top: 0;
}

.boscontent table {
    margin: auto;
}

.boscontent table:first-of-type, .gomangainfo, .mangareadtopad, .content {
    margin: auto;
    padding: 0.5rem 0;
    width: 60rem;
}

.boscontent table:last-child .pagination {
    display: block;
    position: absolute;
    right: 0;
    top: 0;
}

.boscontent table:last-child tbody, .boscontent table:last-child tr {
    display: block;
    position: relative;
    width: 100%;
}

.boscontent table:last-child tr:first-child {
    display: none;
}

.boscontent table:last-child tr:last-child {
    height: 2rem;
    margin-top: 1em;
}

.container {
    background: #333;
    margin: auto;
    text-align: center;
    width: calc(100% - 20rem);
}

.container > table {
    width: 100%;
}

.container > table > tbody, .footer, .boscontent table:last-child {
    display: block;
    margin: auto;
    width: 60rem;
}

.gomangainfo {
    display: none;
}

.mangareadtopad, .NavigateTip {
    display: none;
}

body {
    background: #333;
}

td[valign] {
    min-width: 0;
    padding: 0;
    width: 0;
}
}
