css:
@-moz-document domain("fimfiction.net") {

.user_toolbar {
    background: linear-gradient(to bottom, #7f8e55 0%, #73804b 100%);
    box-shadow: 0px 0px #667243 inset, 0px 0px #667243 inset;
}
.user_toolbar>ul {
    text-align: center;
}
.user_toolbar>ul>li>a.button-first {
    border-left: 1px solid #67743f;
}
.user_toolbar>ul>li>a {
    background: linear-gradient(to bottom, #8b9b5c 0%, #7f8e55 100%);
    border-right: 1px solid #67743f;
    color: #181b10;
    text-shadow: 1px 1px #90a062;
}
.user_toolbar>ul>li>a:hover {
    background: linear-gradient(to bottom, #97a964 0%, #8b9b5c 100%);
    color: #181b10;
    text-shadow: 1px 1px #90a062;
    box-shadow: 0px 0px #7e8d52 inset;
}
.user_toolbar>ul>li>a {
    text-transform: none;
}
.user_toolbar>ul>li:after {
    width: 0px;
}
.user_toolbar>ul>li:before {
    width: 0px;
}

}
