@-moz-document domain("fimfiction.net") {

  .avatar {
      border-radius:5px;
  }

  .avatar.loaded {
      border-radius:5px;
  }

  .user-avatar {
      border-radius:5px;
  }

  .bio_followers ul li .user-avatar {
      border-radius:3px;
  }

  .comment .author .avatar {
      border-radius:5px;
  }

  .user-page-header .avatar-container img {
      border-radius:5px;
  }

  .thread_list table td .avatar {
      border-radius:5px;
  }

  .private-message-list .avatar {
      border-radius:5px;
  }
    
}
