css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("https://sites.google.com/a/wavehill.mygbiz.com/posts-in-classics/") {
  
  #sites-chrome-sidebar-left {
    display: none!important;
  }
  
  #sites-status{
    display: none!important;
  }
  
  #sites-canvas-main{
    padding-top:0px;
  }
  
  .announcements{
    display: none!important;
  }
  
  #sites-chrome-page-wrapper{
    margin-left: 0px;
    margin-right: 0px;
    width: auto!important;
  }
  
  .sites-adminfooter
  {
    display: none!important;
  }
  
  .sites-ccc-nav{
    display: none!important;
  }
  #sites-collaborator-bar{
    display: none!important;
  }
  #sites-canvas-bottom-panel{
    display: none!important;
  }
  
  td.sites-layout-tile.sites-tile-name-content-1{
    padding-left: 0px!important;
    padding-right: 0px!important;
    padding-top: 0px!important;
    padding-bottom: 0px!important;
    margin-right: 0px!important;
    
  }
  div[style] {
   margin-right: 3px!important;
   }
  .sites-embed-showing-results{
    display: none!important;
  }
  
/*   //Text Change */
  
  .sites-announcement-embed-post-title{
    color: #c6c626!important;
    text-decoration: none!important;
    font-family: "din-bold"!important;
  }
  .sites-embed-title{
    background: #b2b50d!important;
    border-radius: 20px 20px 0px 0px;
    border-top: none!important;
    border-bottom: 3px solid #999!important;
  }
  .sites-text-secondary{
    color: #0003;
  }
  
/* border   */
  
     .sites-embed {
    background: #fff;
    width: auto;
    height: auto;
    border-radius: 20px;
  }
 
}
