css:
@-moz-document domain("inoreader.com") {
#reader_pane.reader_pane_sinner {
    padding-right: 0;
}
#reader_pane.reader_pane_view_style_1 .articles_feed_group_heading>.articles_feed_group_heading_title {
    padding: 10px 0 16px 18px;
}
#reader_pane.reader_pane_view_style_1 .articles_feed_group_heading>.articles_feed_group_heading_title.articles_feed_group_heading_title_date {
    padding: 10px 0 16px 18px;
}
.ar {
    margin-bottom: 1px;
    margin-left: 0px;
    margin-right: 0px;
    margin-top: 2px;
}
.article_card .article_sub_title {
    border-bottom: 1px solid #e6e6e6;
    margin: 0;
}
.article_content_rtl {
    float: left;
    padding-left: 12px;
}
.article_footer {
    border-top: 1px solid #e6e6e6;
    margin-top: 0;
}
.article_title[dir="rtl"]+.article_sub_title>.pull_left {
    float: left;
    text-align: left;
}
.article_title_expanded_view {
    direction: ltr;
    text-align: left;
}
.article_unreaded {
    margin-bottom: 1px;
}
.green_state.top_notification,
#sb_rp_upgrade,
.unsubscribed_feed_header,
.unsubscribed_feed_suggestions_label,
.ad_footer_remove,
iframe.avs_iframe {
    display: none;
}
.tree_unseen {
    color: #b45f04;
}
img {
    max-width: 730px;
}
.translation-container {
    display: none !important;
}
div#no_more_div {
    height: calc(100% - 40px) !important;
}
}
