css:
@-moz-document domain("steemit.chat") {
.color-primary-font-color {
    color: rgb(220,220,220);
}

.messages-container .wrapper {
	background-color: rgb(50,50,50);
}

.content-background-color {
    background-color: #444;
}

.messages-container .footer {
    background: #444;
}

input, select, textarea {
    color: rgb(220,220,220);
}

.flex-tab .content, .flex-tab .user-view, .flex-tab .list-view {
    background-color: #444;
}

.first-unread .body::after {
    color: #aaaaaa;
}

.message.new-day::before {
    background-color: rgba(50, 50, 50, 1);
}

.flex-tab .message.new-day::before {
    background-color: #444;
}

.flex-tab-bar .tab-button.active {
    background-color: rgb(127, 127, 127);
}

.message .mention-link.mention-link-me {
    color: rgb(20, 20, 20);
}
}
