css:
@-moz-document domain(saltybet.com) {
    #wrapper {
      position: relative !important;
      padding-top: 600px;
      overflow: auto !important;
    }

    #wrapper > div {
      position: relative;
    }

    #stream {
      height: 600px !important;
      max-width: initial !important;
      float: left !important;
      position: absolute !important;
      top: 42px !important;
      left: 50% !important;
      transform: translateX(-50%);
    }

    .sidebar-view {
      width: 50% !important;
      height: 600px !important;
      float: left !important;
      z-index: 1000;
    }

    #bottomcontent, #footer {
      width: 100% !important;
      top: 42px !important;
      bottom: 0 !important;
    }
}
