css:
@-moz-document domain("www.youtube.com"), domain("youtube.com") {
span.video-thumb.comment-author-thumbnail.yt-thumb.yt-thumb-48 {
    display: none;
}

span.video-thumb.comment-author-thumbnail.yt-thumb.yt-thumb-32 {
    display: none;
}

.comment-renderer-like-count {
    color: green;
}

.comment-author-text {
    color: #03C;
}

span.comment-renderer-author-comment-badge.creator {
    color: #03C;
    padding: 0px 0px;
    background-color: white;
}

.comment-renderer-author-comment-badge.creator .comment-author-text {
    color: #03C;
}

.comment-renderer-header .comment-renderer-time a:before {
    content: "(";
}

.comment-renderer-header .comment-renderer-time a:after {
    content: ")";
}

#comment-section-renderer .sprite-dislike:before {
    background: transparent url(http://i.imgur.com/b8Z7acM.png) no-repeat scroll -385px 0px;
    width: 20px;
    overflow: visible;
    margin-top: -6px;
    height: 20px;
}
#comment-section-renderer .sprite-like:before {
    background: transparent url(http://i.imgur.com/b8Z7acM.png) no-repeat scroll -404px 1px;
    width: 20px;
    overflow: visible;
    margin-top: -6px;
    height: 20px;
}

#comment-section-renderer .sprite-like[aria-checked="true"]:before {
    background: transparent url(http://i.imgur.com/b8Z7acM.png) no-repeat scroll -404px -18px;
    width: 20px;
    overflow: visible;
    margin-top: -6px;
    height: 20px;
}

#comment-section-renderer .sprite-dislike[aria-checked="true"]:before {
    background: transparent url(http://i.imgur.com/b8Z7acM.png) no-repeat scroll -385px -19px;
    width: 20px;
    overflow: visible;
    margin-top: -6px;
    height: 20px;
}


.comment-renderer-header {
    width: 80%;
}

.comment-renderer-text {
    width: 75%;
}

.comment-action-buttons-toolbar {
    float: right;
}

div#comment-simplebox {
    width: 100%;
}
.comment-action-buttons-toolbar {
    float: right;
    margin-top: -30px;
}


button.yt-uix-button.yt-uix-button-size-default.yt-uix-button-default.yt-uix-button-empty.yt-uix-button-has-icon.no-icon-markup.comment-action-buttons-renderer-thumb.yt-uix-sessionlink.sprite-comment-actions.sprite-like.i-a-v-sprite-like {
    filter: none !important;
}

button.yt-uix-button.yt-uix-button-size-default.yt-uix-button-default.yt-uix-button-empty.yt-uix-button-has-icon.no-icon-markup.comment-action-buttons-renderer-thumb.yt-uix-sessionlink.sprite-comment-actions.sprite-dislike.i-a-v-sprite-dislike {
    filter: none !important;
}

}
