css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wiki.python.org") {
body 
{
  
  background-color: #151515;
  color: white;
  
}
h1, h2, h3, h4, h5
{
  
  color: #F5DB1C;
  
}
body a:visited
{
  
  color: red;
  
}
a:link
{
  
  color: #EE9700
  
}
#sidebar
{
  
  background-color: #202020;
  
}
div#sidebar h1
{
  
  color: white;
  
}
div#sidebar ul 
{
  
  color: white;
  
}
div#sidebar ul li a
{
  
  color: #EE9700;
  
}
div#header 
{

  background: #121212;
  color: white;

}
ul#pagetrail li a, #locationline ul li a 
{
  
    color: #EE9700;
    
}

}
