css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mrakopedia.org") {
body, #column-content, #column-one, .pBody
                   { background: #191919 !important;  }
#column-one        { padding-top: 40px !important; }
#p-logo, #footer   { display: none !important; }
a                  { color: #c3c7ca !important; }
.pBody             { margin-left: 5px !important;
                     border-width: 0px !important; }
#bodyContent, #content, #searchBody, .toc
                   { background: #222222 !important;
                     color: #8d8d8d !important;
                     border-color: #333333 !important; }
.thumbinner, .thumbimage, .thumb        
                   { background-color: #191919 !important;
                     border-color: #333333 !important; }
h3                 { color: white !important; }
h1, .mw-headline, h2                 
                   { color: #b1b5b8 !important; }
#ca-nstab-main > a, #ca-talk > a, #ca-viewsource > a, #ca-history > a, #ca-purge > a, #ca-edit > a, #catlinks, #description, #ca-nstab-category > a, #ca-masseditregex > a
                   { background: black !important; }
.box               { background: grey !important; }
.rating_text       { color: black !important; }
.rating_text_text  { margin-top: -6px !important;
                     margin-left: 10px !important; }
}
