css:
@-moz-document domain("www.axios.com") {
.widget__image-container{
	display: none;
}

.widget__footer{
	display: none;
}

.main__header{
	display: none !important;
}

.content{
	margin: 0px;
}

.author-avatar{
	margin: 0px !important;
	padding: 0px !important;
	margin-bottom: 20px !important
}

.author-avatar__avatar{
	display: none;
}

.axios-post{
	margin-bottom: 40px;	
}

.widget{
	padding: 15px !important;
	margin: 0px !important;
}


.axios-post .animate-keep-reading{
	opacity: 1;
	display: block;
}

.widget__keep-reading-action{
	display: none !important;
}

p{
	background: #fff;
}

.content__secondary{
	display: none;
}

.posts-main--featured .widget:not(.posts-read--another) > article:not(.posts-flagged):before{
	display: none;
}

.content__main{
	max-width: 800px;
	margin: 0px auto;
	padding: 0px !important;
	float: none;
}

.rebelbar--fixed{
	position: absolute !important;
}


}
