css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("reddit.com") {
  .side, .trending-subreddits, .listing-chooser, #sr-header-area, #header-bottom-right {
    display: none !important;
  }
  body, .link .title, .tagline, .domain, .author, .tagline time, .rank, .score{
    font-size: 1.3em !important;
  }
  .md p, .usertext-body, .md-container {
    font-size: 1.4em !important;
  }
  .content {
    width: 100% !important;
  }
  .expando-button, .arrow {
    transform: scale(1.2) !important;
  }
  .thumbnail:not(.image):not(.self):not(.default), .thumbnail:not(.image) img{
    width: 140px !important;
    height: 104px !important;
  }
}
