css:
@-moz-document domain("twitter.com") {
.RichEditor{
     border-radius: 0px!important;
  }
  img.avatar,
  img.ProfileAvatar-image,
  img.ProfileCard-avatarImage
  {
    border-radius: 2px!important;
    border: none!important;
  }
  
  img.DashboardProfileCard-avatarImage.js-action-profile-avatar
  {
    border-radius: 5px!important;
    border: solid 1px!important;
    border-color: #e3e5ff!important;
  }
  
  button.tweet-action.EdgeButton.EdgeButton--primary.js-tweet-btn, 
  button#global-new-tweet-button.js-global-new-tweet.js-tooltip.EdgeButton.EdgeButton--primary.js-dynamic-tooltip
  {
    border-radius: 5px!important;
  }
  
  button
  {
    border-radius: 5px!important;
  }

  h3
  {
   font-weight: normal!important; 
  }

  span.trend-location.js-trend-location, span.ProfileCardStats-statLabel.u-block
  {
    font-weight: normal!important;
  }
}
