css:
@-moz-document url-prefix(https://twitter.com) {
  .ProfileAvatar-image {
      animation: spin 2s linear infinite;
  }

  .ProfileAvatar:after {
      content: url(https://pbs.twimg.com/media/DCYsp7SUMAEnZTJ.png:orig);
      position: absolute;
  }

  @keyframes spin {
      0% {
          transform: rotate(0deg);
      }
      100% {
          transform: rotate(360deg);
      }
  }
}
