css:
@-moz-document domain("twitter.com") {
.edge-design .avatar {
    border-radius: 5%!important;
}

.edge-design .Avatar, .edge-design .DashboardProfileCard-avatarImage, .edge-design .DashboardProfileCard-avatarLink, .edge-design .Gallery.is-tweetless .Gallery-content, .edge-design .Gallery.is-tweetless .Gallery-media, .edge-design .MomentCapsuleCover .MomentUserByline-avatar, .edge-design .MomentCapsuleItemTweet--withText .MomentUserByline-avatar, .edge-design .MomentCapsuleSummary .MomentUserByline-avatar, .edge-design .MomentMakerRecommendedTweetsSearch--users .MomentMakerRecommendedTweetsSearch-userContainer .avatar, .edge-design .ProfileAvatar, .edge-design .ProfileAvatar-image, .edge-design .ProfileAvatar-placeholderImage, .edge-design .ProfileAvatarEditing, .edge-design .ProfileAvatarEditing-button, .edge-design .ProfileAvatarEditing-overlay, .edge-design .ProfileCard-avatarImage, .edge-design .ProfileCard-avatarLink, .edge-design .ProfileCardMini-avatarImage, .edge-design .ProfileListItem-avatar, .edge-design .ProfileUserList .Avatar, .edge-design .RelatedUsers-users>.account-summary .avatar {
    border-radius: 5%!important;
    border-top-left-radius: 5%!important;
    border-top-right-radius: 5%!important;
    border-bottom-right-radius: 5%!important;
    border-bottom-left-radius: 5%!important;
}

.nav .session .dropdown-toggle {
    border: 0;
    border-radius: 5%!important;
    opacity: 1;
    -ms-filter: "alpha(opacity=100)";
}

.nav .session .dropdown-toggle:before {
    border-radius: 5%!important;
    box-shadow: inset 0 0 0 1px rgba(0,0,0,0.1);
    content: "";
    display: block;
    height: 100%;
    position: absolute;
    width: 100%;
}
}
