css:
@namespace url(http://www.w3.org/1999/xhtml);
/*
* Author: Ew0345
* Note: Selecting comics is slightly weird with this style but doesn't acutally effect the comics. If you want me to fix it let me know.
* Version: 1.0b
*/
@-moz-document domain("moonbunnycafe.com") {
  body {
    background-color: #1f1f1f !important;
  }
  #header {
    background-color: #161616 !important;
  }
  .menu-item,
  .menu-item a:link,
  .menu-item a:visited {
    background-color: #1f1f1f !important;
    color: #eee !important;
  }
  .entry-title a:link,
  .entry-title a:visited,
  .entry-content a:link,
  .entry-content a:visited {
    color: #eee !important;
  }
  .menu-item a:hover,
  .entry-title a:hover, 
  .entry-content a:hover {
    color: #47a7d7 !important;
  }
  #main,
  #container,
  #content,
  #s,
  #cat,
  .level-0,
  .entry-meta {
    background-color: #232323 !important;
    border-color: #1c1c1c !important;
  }
  .entry-title,
  .entry-content,
  .entry-content p span b,
  .entry-summary,
  .entry-meta,
  .level-0, 
  #s,
  #cat {
    color: #eee !important;
  }
  #primary.widget-area {
    background-color: #202020 !important;
    color: #eee !important;
  }
  #cat {
    border-style: ridge !important;
  }
  #divSliderMain {
    display: none !important;
  }
  .comments-link {
    background-color: #1f1f1f !important;
    box-shadow: inset 1px 1px 1px #ccc !important;
  }
  .commentlist,
  .comment,
  .fn,
  .says, 
  .comment-body {
    background-color: #1f1f1f !important;
    color: #eee !important;
    border-color: #1e1e1e !important;
  }
  #comment,
  #author, 
  #email, 
  #url {
    background-color: #252525 !important;
  }
  #submit.submit {
    background-color: #272727 !important;
    color: #eee !important;
    box-shadow: none !important;
  }
  .pagination,
  .pagination a:link,
  .pagination a:visited {
    background-color: #1a1a1a !important;
  }
  .pagination a:hover {
    background-color: #777 !important;
  }
}
