css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.rtbf.be") {
  
  .content-main {
    background-color: #222 !important;
  }
  
  .rtbf-article-grid__container {
    background-color: #333 !important;
    color: red !important;
  }
  
  .rtbf-article-main {
    font-family: Verdana, "sans-serif" !important;
    
  }
  .rtbf-article-main em {
    font-style: normal !important;
  }
  .rtbf-article-main__content {
    font-size: 1em;
  }
  
  .content-main > div > article {
    color: #EEE !important;
    background-color: #333;
  }
  .content-main > div > article > header > h1{
    color: rgb(0, 160, 249);
  }
  
  /* Subnav */
  .rtbf-site-subnav {
    background-color: rgb(0, 80, 149);
  }
  .rtbf-site-subnav a {
    color: #CCC;
  }
  .rtbf-site-subnav a.rtbf-site-subnav__cat-main {
    color: rgb(0, 160, 249);
  }
  .rtbf-site-subnav a.current {
    color: white;
  }
  
  .content-main .container-fluid {
    background-color: #333;
  }
  .rtbf-article-grid__title-item > a {
    color: #CCC;
  }
  
  article h3 > a {
    color: #0AF !important;
  }
  
  article > p {
    color: white !important;
  }
}
