 * Author:			Atx
 * Version:			v1.1
 * Latest update:	26.08.2017
 */

@-moz-document domain("suprnova.cc")
{
  .page-header-fixed, body,
   .page-header.navbar .top-menu .navbar-nav > li.dropdown-dark .dropdown-menu.dropdown-menu-default > li a:hover,
  .page-header.navbar .top-menu .navbar-nav > li.dropdown-dark .dropdown-menu .dropdown-menu-list > li a:hover,
  .page-sidebar, .table-striped > tbody > tr:nth-child(2n+1),
  .page-sidebar-closed.page-sidebar-fixed .page-sidebar:hover
  {
   background-color: #1C1C1C;
  }
  
  .portlet.light, .form-control, .form-control[disabled], .input-group .input-group-addon, .form-control.form-control-solid,
  .container .jumbotron, .well, .navbar-default
  {
   background-color: #2C2C2C;
   color: #888888;
  }

  .well, .navbar-default
  {
   background-image: none !important;
  }
  
  .portlet-body
  {
    color: #CCCECF;
  }
  
  .page-sidebar.navbar-collapse, .panel > div:nth-child(2)
  {
    background-color: #2C2C2C;
  }
  
.table-striped > tbody > tr:nth-child(2n+1) > td, .table-striped > tbody > tr:nth-child(2n+1) > th, .table-scrollable > .table, .navbar-default
  {
    background-color: #3C3C3C;
  }
  
   #motd > b:nth-child(6) > pre:nth-child(3)
  {
    background-color: #1C1C1C;
    color: #888888;
  }

  .page-header.navbar
  {
    border-bottom: 1px solid #2C2C2C;
  }
  
  .alert-success
  {
    background-color: #273225;
    border-color: #273225;
    color: #49994A;
  }
  
  .page-header.navbar .top-menu .navbar-nav > li.dropdown-dark .dropdown-menu
  {
    background-color: #3C3C3C;
    border: 2px solid #2C2C2C;
  }

  .page-header.navbar .top-menu .navbar-nav > li.dropdown-dark .dropdown-menu.dropdown-menu-default > li.divider
  {
    background: #000000;
  }

  .alert-success
  {
    color: #46AE43 !important;
  }
  
  #motd > b:nth-child(6) > font:nth-child(1)
  {
    color: #5B9BD1;
  }
  
   #motd > b:nth-of-type(2) > font, article.module.width_full
  {
     color: #D14444;
  }

  .page-sidebar .page-sidebar-menu > li:hover > a, .page-sidebar .page-sidebar-menu > li.open > a,
  .page-sidebar-closed.page-sidebar-fixed .page-sidebar:hover .page-sidebar-menu > li.active > a,
  .page-sidebar-closed.page-sidebar-fixed .page-sidebar:hover .page-sidebar-menu > li.active.open > a,
  .page-sidebar .page-sidebar-menu > li.active > a, .page-sidebar .page-sidebar-menu > li.active.open > a
  .page-sidebar-closed.page-sidebar-fixed .page-sidebar:hover .page-sidebar-menu .sub-menu > li.open > a,
  .page-sidebar-closed.page-sidebar-fixed .page-sidebar:hover .page-sidebar-menu .sub-menu > li:hover > a,
  .page-sidebar-closed.page-sidebar-fixed .page-sidebar:hover .page-sidebar-menu .sub-menu > li.active > a,
  .page-sidebar .page-sidebar-menu .sub-menu > li:hover > a, .page-sidebar .page-sidebar-menu .sub-menu > li.open > a,
  .page-sidebar .page-sidebar-menu .sub-menu > li.active > a
  {
    background: #2E363E !important;
  }
  

  #mine > div:nth-child(1), .footer
  {
     color: #888888;
  }
  
  .label-warning
  {
    background-color: #8D7428;
  }
  
 .table-striped > tbody > tr:nth-child(2n+1):hover
  {
    background-color: #202020;
  }
 .table-striped > tbody > tr:nth-child(2n):hover
  {
    background-color: #323232;
  }
  
.panel .panel-body
  {
    color: #959595;
  }
}
