css:
@-moz-document domain("web.wechat.com") {
.chat_list {
    background: -webkit-linear-gradient(#000000 10%, #0d0d0d 50%, #1a1a1a 90%);
    ;
    color: #bfbfbf;
}
.box_hd .title .title_name {
    color: #e6e6e6;
}
.box_hd .title_wrap {
    background: #000;
    border-bottom: 1px solid #333333;
}
.tab .tab_item a {
    background: #000;
    color: #808080;
}
.no-choose .catch-drop-area {
    background: -webkit-radial-gradient(#333333 10%, #1a1a1a 50%, #000000 90%);
}
.header {
    background: -webkit-radial-gradient(#333333 10%, #1a1a1a 50%, #000000 90%);
}
.panel {
    background: #000000;
}
.dropdown_menu li a {
    background: none!important;
    color: #bfbfbf;
}
.dropdown_menu li a {
    background: #000;
}
.chat .box_ft {
    background: -webkit-linear-gradient(#000000 10%, #0d0d0d 50%, #1a1a1a 90%);
    color: #bfbfbf;
}
.chat .box_ft .toolbar {
    background: #000;
}
.box {
    background: -webkit-radial-gradient(#333333 10%, #1a1a1a 50%, #000000 90%);
}
.contact_list .contact_title {
    background: -webkit-linear-gradient(#333333 10%, #1a1a1a 50%, #000000 90%);
}
.members {
    background: #000;
    border-bottom: 1px solid #333333;
    color: #bfbfbf;
}
.search_bar .frm_search {
    background: #262626;
}

.btn_send
{
    background: -webkit-radial-gradient(#333333 10%, #1a1a1a 50%, #000000 90%);
    color: #bfbfbf;
}
}
