/* This style is created as a Global style and will only effect btsync. */

.isWebui .verticalScrollContainer
{
background: #111111 !important;
}
.isWebui .table tr:hover,
.isWebui th,
.isWebui #sync-core-state,
.isWebui .modal-body,
.isWebui .nav,
.isWebui .modal-footer,
.isWebui .directory-entry a:hover,
.isWebui .form-control
{
