css:
/* i really want this to be global */

@-moz-document url-prefix("https://dbwebb.se") {
  p,
  h1,
  h2,
  h4,
  h5,
  h6 {
      color: white;
  }
  h3 {
      color: #FF8C00;
  }
  .block h4 {
      color: FireBrick ;
  }
  ol {
      color: #FF8C00;
  }
  body {
      background-color: #1b1b1d;
  }
  .profile .login a {
      color: dodgerblue;
  }
  div .sidebar .block h4 {
      color: black;
      background-image: linear-gradient(to right, #fff, #1b1b1d);
      border-top: solid 1px black;
  }
  .logo-1 {
      display: none;
  }
  ul,
  a {
      color: dodgerblue;
  }
  code {
      color: black;
  }
  .info p {
      color: black;
  }
  .rm-navbar-top .rm-desktop a {
      color: white;
  }
  .site-logo-text a {
      color: grey;
      font-family: Verdana, Geneva, sans-serif;
  }
  .warning p {
      color: black;
  }
  table {
      color: LightSeaGreen;
  }
  label {
      color: white;
  }
  dd {
      color: dodgerblue;
  }
  dd label {
      color: white;
  }
  div .content {
      color: white;
  }
  div .content div {
      color: black;
  }
}
