css:
@-moz-document domain("fi.nobelium.xyz") {
.card-block {
    border: 1px solid black;
    background-color: #eeeeee;
}

.card-block a {
	color: #6e99c9;
}

.btn-secondary {
	border: 1px solid black;
    color: rgb(85, 85, 85)!important;
}

.btn-secondary:hover {
	background-color: #6e99c9;
    color: white!important;
    border: 1px solid black;
    box-shadow: none;
}

.btn-danger {
	background-color: white;
    border: 1px solid black;
    color: rgb(85, 85, 85)!important;
}

.btn-primary:hover {
	color: white!important;
}

.btn-primary {
	border: 1px solid black;
    color: rgb(255, 255, 255)!important;
    background-color: #6e99c9!important;
    box-shadow: none!important;
}

.btn-danger:hover {
	background-color: #6e99c9;
    color: white!important;
    border: 1px solid black;
    box-shadow: none;
}

.btn-success {
	color: rgb(85, 85, 85)!important;
}

thead {
	border: 1px solid black;
    background-color: #eeeeee;
    border-bottom: 1px solid black;
}

.btn-success:hover {
	box-shadow: none;
    color: white!important;
}

.btn-success a {
	color: rgb(85, 85, 85)!important;
}

.n-games-btn {
	border: 1px solid black;
    background-color: white;
    color: rgb(85, 85, 85);
}

.n-games-btn:hover {
	background-color: #6e99c9;
    box-shadow: none;
    border: 1px solid black;
    color: white;
}

.card-block {
	color: rgb(85, 85, 85);
}

.text-success {
	color: #6e99c9!important;
}

body {
    font-family: Comic Sans MS;
}

.navbar-light.bg-faded {
    background-color: #6e99c9;
    color: white!important;
    border-bottom: 1px solid black;
}

.navbar-light.bg-faded a {
    color: white!important;
    font-weight: bold;
}

.navbar-nav a {
	color: white;
    font-weight: bold;
}

.nav-item a:hover {
    text-decoration: underline;
}

body {
	background: white;
}

.custom-control-indicator:checked {
	background-color: #6e99c9;
}

a {
	color: #6e99c9;
}

.navbar-nobelium-dropdown-item-menu a {
	color: rgb(85, 85, 85)!important;
}

.card-header {
	color: white;
}

.btn-outline-primary:hover {
	background-color: #6e99c9;
    color: white;
    box-shadow: none;
    border: 1px solid black;
}

.btn-outline-primary {
	border: 1px solid black;
    background-color: white;
    color: rgb(85, 85, 85);
}

.btn-primary a {
	color: rgb(85, 85, 85);
}

.btn-primary:hover {
	background-color: #6e99c9;
    border: 1px solid black;
    box-shadow: none;
}

.btn-primary {
	color: rgb(85,85,85);
    background-color: white;
    border: 1px solid black;
}

.footer >.container {
	background-color: #eeeeee;
    border: 1px solid black;
    color: rgb(85, 85, 85);
}

.footer >.container a {
	color: #6e99c9;
}

.btn-success {
	background-color: white;
    border: 1px solid black;
    color: rgb(85, 85, 85);
}

.trustedText {
	color: #6e99c9;
}

.btn-success:hover {
	background-color: #6e99c9;
    border: 1px solid black;
    color: white;
}

.btn-success a {
	color: rgb(85, 85, 85);
}

.btn-success a:hover {
	background-color: #6e99c9;
    color: white;
}

.nav-link a {
	color: white;
    font-weight: bold;
}

.dropdown-menu {
	color: rgb(85,85,85)!important;
}

.nav-link a:hover {
    text-decoration: underline;
}

.navbar-light.bg-faded a:hover {
    text-decoration: underline;
    color: white;
}

.card-header {
	background-color: #6e99c9;
    border: 1px solid black;
    border-bottom: none;
    border-radius: 0;
}
}
