css:
@-moz-document url-prefix("http://qiita.com/") {
/* ===== 見出し 
水　赤　緑 桃 */
section>h1,
section>h2,
section>h3,
section>h4,
section>h5,
section>h6{
   position: relative;
}
section>h1{
    margin-left:-20px !important;
}

section>h1::after{
width: 120px;
height: 3px;
position: absolute;
bottom: -3px;
background-image:linear-gradient(to right,#33dfdf 40%,#05150e 100%);
left:0;
content: '';
}
section>h2::after{
width: 100px;
height: 3px;
position: absolute;
bottom: -3px;
background-image:linear-gradient(to right,#ef0222 40%,#05150e 100%);
left:0;
content: '';
}
section>h3{
    margin-left:20px !important;
}
section>h3::after{
width: 80px;
height: 3px;
position: absolute;
bottom: -3px;
background-image:linear-gradient(to right,#34db25 40%,#05150e 100%);
left:0;
content: '';
}
section>h4{
    margin-left:40px !important;
}
section>h4::after{
width: 60px;
height: 3px;
position: absolute;
bottom: -3px;
background-image:linear-gradient(to right,#ff2de1 40%,#05150e 100%);
left:0;
content: '';
}
/* ついてくるツリー色 */
div.tocTree_contents{
    background: hsla(19,0%,4%,.75);
}
.tocTree_contents a{
    color:#fff !important;
}
.tocTree_contents .active > a, .tocTree_contents a:hover{
    background: hsla(190,100%,80%,.4);
}
/*　===== 引用 ===== */
blockquote{
    background: #f4f4f4;
}
pre{
    background: #e6e6fa !important;
}
/* ========== 広告除去 ========== */
.scroll-chaser,.adsbygoogle
,.col-sm-3>div:first-child
,.col-sm-9>div:first-child{
    display:none !important;
}

@media screen and (min-width:1399px) {
div.tocTree{
    width:400px !important;  
}
}
}
