css:
@-moz-document domain('nicehash.com') {
    body {
        background-color: #000;
        color: #fff;
    }
    header nav.top {
        background: none;
        background-color: #111;
    }
    #intro.index {
        background-color: #000;
    }
    #intro .tiles .tile {
        background-color: #333;
    }
    .page.profile .row.overflow #stats .col_4 h2 {
        background-color: #111;
        border: 1px solid #333;
    }
    .page.profile .row.overflow #stats .col_4 .content {
        border: 1px solid #333;
    }
    .page.profile .col_1_3 .payout .payout_content {
        border: 1px solid #333;
    }
    .page .select select {
        background-color: #999;
    }
    .page table.light {
        border: 1px solid #333;
    }
    .page.profile .col_2_3 table.light tr td.results {
        border-top: 1px solid #333;
    }
    .page table.light tr th {
        color: #ccc;
        border: 1px solid #333;
    }
    .page table tr th {
        background-color: #111;
    }
    .page table tr:nth-child(2n+1) {
        background-color: #333;
    }
    .page table.inside tr td {
        color: #999;
    }
    .page #stats {
        border: 1px solid #333;
    }
    .page .sorting_row {
        border-bottom: 1px solid #333;
    }
    .page .pages .prev,
    .page .pages .next {
        color: #999;
    }
}
