css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("comic.sfacg.com") {

  body {
    position: relative;
  }
  
  .Reduction_top {
    display: none;
  }
  
  .wrap {
    padding-top: 0 !important;;
    position: fixed;
    top: 0;
    height: 5px;
    background-color: #777;
    overflow: hidden;
    width: 100%;
  }
  
  .wrap:hover {
    height: auto;
    background-color: rgba( 127,127,127,.5 );
  }
  
  #curPic {
    height: 100vh;
    min-height: 33vw;
    position: absolute;
    top: 0;
    left: 33vw;
  }
  
}
