css:
@-moz-document domain("youtube.com") {
ytd-thumbnail-overlay-playback-status-renderer {
    width: 100%;
    height: 100%;
}

yt-formatted-string.ytd-thumbnail-overlay-playback-status-renderer {
    margin: 0;
    opacity: .9;
    padding: 25% 0;
    border-radius: 0px;
    font-size: 1.7rem;
    display: block;
    width: 100%;
    height: 100%;
    text-align: center;
    transition: opacity 200ms ease-out;
    color: #999;
}

yt-formatted-string.ytd-thumbnail-overlay-playback-status-renderer:hover {
    opacity: 0;
}

yt-formatted-string.ytd-thumbnail-overlay-playback-status-renderer {
    background-color: rgb(255, 255, 255);
}

body[dark=true] yt-formatted-string.ytd-thumbnail-overlay-playback-status-renderer {
    background-color: rgb(17, 17, 17);
}
}
