css:
@-moz-document url-prefix("https://www.beeminder.com/") {
.goal.orange {
  background-color: #f89406 !important;
}
.goal {
  color: white;
}
.goal.red {
  background-color: #ca1212 !important;
}
.goal.green {
  background-color: #00ad00 !important;
}
.goal.blue {
  background-color: #125bca !important;
}
a.sort-button {
  color: #999 !important;
}
.dashboard.content .archive-link, .dashboard.content .archive-link a {
	color: #777;
}
.dashboard-header-wrapper {
  background-color: transparent !important;
}
.content.dashboard.dashboard-header {
  background-color: #3b3b3b;
}
a.minithumb img {
    filter: invert(77%);
    left: -2px;
    top: -2px;
    position: relative;
    margin-right: -4px;
    margin-bottom: -8px;
}

a.minithumb {
    border: 2px solid #3b3b3b;
    overflow: hidden;
}
}
