css:
@-moz-document regexp("http://www.network54.com/Forum/176899.*") {
body,
tr,
td {
	background: #F4ECD8 !important;
	color: #5B4636;
}
a {
	color: #9d6536;
}
table,
thead,
tbody,
tr,
td {
	display: block;
	width: auto;
	height: auto;
	border-radius: inherit;
}

/*td[bgcolor="#6699FF"]:nth-child(odd) {}*/
#n54firstpost {
	display: none;
}
@media only screen and (min-width: 750px) {
	td[bgcolor="5D5D5D"] {
		max-width: 700px;
		margin-left: calc(50vw - 375px);
	}
}
td[bgcolor="5D5D5D"] tr {
	display: block;
	border: 1px solid #5b4636;
	border-radius: 3px;
	margin-bottom: 10px;
}
td[bgcolor="5D5D5D"] tr tr {
	border: none;
	margin-bottom: 0;
}
.KonaBody {
	font-size: 1.1em;
	line-height: 1.5em;
	text-align: left;
	display: inline;
}
div[style="float:left;width:350px;height:240px;margin-bottom:24px;"] {
	display: none !important;
}

/*td[bgcolor="5D5D5D"] tr td[width="17.9%"] {}*/
}
