css:
@-moz-document domain(repl.it) {
    .ace-tm {
     	background-color: #212121;
        color: #f8f8f8;
    }
    .ace-tm .ace_storage, .ace-tm .ace_keyword {
        color: #ff4bb1;
    }
    .ace-tm .ace_constant {
        color: rgb(52, 76, 252);
    }
    .ace-tm {
        color: #f8f8f8;
    }
    .ace_punctuation.ace_operator {
        color: #fb4578;
    }
    .ace_storage.ace_type {
        color: #1d9bd5;
    }
    .ace_paren.ace_lparen {
        color: #ff9731;
    }
    .ace_paren.ace_rparen {
        color: #ff9731;
    }
    .ace-tm .ace_constant.ace_numeric {
        color: rgb(255, 161, 40);
    }
    .ace-tm .ace_string {
        color: rgb(87, 236, 93);
    }
    .ace-tm .ace_gutter {
        background: #151515;
        color: #606060;
    }
    .ace-tm .ace_gutter-active-line {
        background-color: #2c2c2c;
    }
}
