/*AGENT SHEET*/
body, html
{
	background-color: #333;
    color: lightgrey;
}

button
{
    background-color: #c3c3c3;
    border-radius: 5px;
}

img
{
	display: none;
}

input, select
{
	opacity: 0.75;
    filter: drop-shadow(0 0 1px none) !important;
}

#header
{
	border-right: #aaa solid 0.5px;
    background-color: rgba(255,255,255,0.17);
}

/* CSS Editor */
.cm-s-3024-night span.cm-tag
{
	color: #a55;
    text-shadow: 0 0 3px red;
}
.cm-s-3024-night span.cm-property, .cm-s-3024-night span.cm-attribute
{
	color: #5a5;
    text-shadow: 0 0 3px green;
}
.cm-s-3024-night span.cm-variable
{
	color: #5aa;
    text-shadow: 0 0 3px cyan;
}
.cm-s-3024-night span.cm-keyword
{
	color: #aa5;
    text-shadow: 0 0 3px yellow;
}
.cm-s-3024-night span.cm-number
{
	color: #a7a;
    text-shadow: 0 0 3px #c9c;
}

.cm-s-3024-night span.cm-comment
{
    color: #666;
    text-shadow: 0 0 3px #888;
}

