css:
@-moz-document domain(app.salesflare.com) {

/* right sidebar */
.button-group {padding: 0 8px;}

/* pipeline */
/* .pipeline-select { width: 220px; } */
#opportunities md-list-item 				{ border: none; margin: 10px 0 0 6px; border-radius: 7px; padding: 5px 0px 5px 6px; box-shadow: 0px 1px 1px 0px rgba(130,145,153,0.5) }

/* opportunities pipeline */
#opportunities .opportunities-header 		{color: #000000!important; font-family: Avenir; font-weight: 500!important; }
#opportunities md-list-item h1,
#opportunities md-list-item h2 				{ text-rendering: geometricPrecision; font-weight: 500; font-family: Avenir; }

@media screen and (min-width: 960px) {
	#opportunities>div.layout-column 		{ min-width: 270px; }
	#opportunities .opportunities-header 	{ font-size: 16px; }
	#opportunities sf-avatar 				{ margin: 0 5px 0 0; }
	#opportunities sf-avatar.sf-avatar_mini { display: none; }
	#opportunities md-list-item h1 			{ color: #3D474D; font-size: 1.1rem; }
	#opportunities md-list-item h2 			{ color: #1D474D; font-size: 1rem }
}
  
}
