css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.usa-undernet.com") {
  body, canvas, .wrap, li, ul, .navbar_footer, .copyright_bar {
    background-color: #4a4a4a !important; 
    color: grey !important;
  }
  a {  
    color: #ECECEC !important;
  }
  .scrollToTop { 
    color: darkgoldenrod !important; 
  }
  input.inputbox { 
    background-color: #636565 !important;
    color: #ECECEC !important;
  }
  #inner-wrap, .fancy_panel_padding, .viewtopic_wrapper, .post, .panel { 
    background-color: #5a5a5a !important;
    color: #ECECEC !important;
  }
  .profile-rank { 
    color: #ECECEC !important; 
  }
  #mchat-panel, #mchat-status { 
    background-color: #4a4a4a !important; 
  }
  span { 
    color: #ECECEC !important;
  }
}
