css:
/* i really want this to be global */
body.active-brand #wrapper, body.active-brand #footer {
    width: 100%;
}

body.has-brand.pp {
    padding-top: 0px !important;
}

.b-content__columns {
    padding-right: 0;
}

.b-content__inline_inner_mainprobar {
    padding-right: 0;
}
.b-content__inline_sideprobar {
    display: none;
}

.b-content__inline_item {
    width: 155px;
}

.b-post__support_holder {
    color: #fff;
}

.b-sidetitle, .b-post__mtitle, .b-post .b-sidelist {
    text-align: center;
}

.b-sidelist .b-content__inline_item {
    margin-left: 12px;
    width: 150px;
}
.b-content__inline_item-cover img {
	height: auto; 
    width: 100%; 
}

.b-player iframe {
    width: 100%;
    height: 450px;
}
div#ownplayer {
    width: 100% !important;
    height: 450px !important;
}
#videoplayer, #youtubeplayer {
    width: 98% !important;
    height: auto !important;
}
div#videoplayer {
    height: auto !important;
}

.b-simple_episodes__list {
    padding: 10px 10px 10px 13px;
}

.b-content__sidebar,
.b-post__social_holder,
DIV.b-container.b-wrapper:nth-child(4) > DIV.b-post > DIV.b-content__columns.pdt.clearfix:last-child > DIV.b-content__main:first-child > DIV:nth-child(6),
DIV.b-container.b-wrapper:nth-child(4) > DIV.b-post > DIV.b-content__columns.pdt.clearfix:last-child > DIV.b-content__main:first-child > DIV:nth-child(5),
DIV.b-container.b-content.b-wrapper:last-child > DIV.b-content__inline:nth-child(2) > DIV.b-content__inline_inner.b-content__inline_inner_main.clearfix > DIV.b-content__inline_sidebar:last-child > DIV.b-seriesupdate__block:nth-child(2) > UL.b-seriesupdate__block_list:last-child > LI:nth-child(2),
.b-wrapper.nopadd,
.skin-block
{
    display: none;
}
