css:
/* AGENT_SHEET */
/** 
*/

html scrollbar {
	background-color: rgba(30,30,30,1) !important;
}

html scrollbar[orient="vertical"]{
	width: 10px !important;
}
html scrollbar[orient="horizontal"]{
	height: 10px !important;
}

html scrollbar[orient="vertical"] gripper {
	width: 10px !important;
}
html scrollbar[orient="horizontal"] gripper {
	margin-top: 0px !important;
	height: 10px !important;
}

html thumb gripper,
html thumb[height]:not([width]) gripper{
    background: rgba(40,40,40,0.5) !important;
}
html thumb:hover gripper,
html thumb[height]:not([width]):hover gripper{
	background: rgba(90,90,90,0.8) !important;

}
html thumb:active gripper,
html thumb[height]:not([width]):active gripper{
	background: rgba(130,130,130,0.8) !important;

}

html thumb[orient="vertical"],
html thumb[height]:not([width]) {
	min-height: 10px !important;
	background: rgba(120,120,120,0.5) !important;
	border: 0px !important;
	box-shadow: none !important;
}

html thumb[orient="horizontal"],
html thumb[width]:not([height]) {
	min-width: 24px !important;
	background: rgba(120,120,120,0.5) !important;
	border: 0px !important;
	box-shadow: none !important;
}

html scrollbarbutton[type="increment"] {
	margin: 0px -4px 0px 0px/*0px*/ !important;
	min-width: 10px !important;
	min-height: 10px !important;
	background: rgba(30,30,30,1) !important;
}
html scrollbarbutton[type="increment"]:hover {
	background: rgba(90,90,90,0.8) !important;
}
html scrollbarbutton[type="increment"]:hover:active {
	background:  rgba(130,130,130,0.8) !important;
}
html scrollbar[orient="vertical"] > scrollbarbutton[type="increment"] {
	margin: 0px 0px -4px 0px !important;
	min-width: 10px !important;
	min-height: 10px !important;
	background: rgba(30,30,30,1) !important;
}
html scrollbar[orient="vertical"] > scrollbarbutton[type="increment"]:hover {
	background: rgba(90,90,90,0.8) !important;
}
html scrollbar[orient="vertical"] > scrollbarbutton[type="increment"]:hover:active {
	background:  rgba(130,130,130,0.8) !important;
}


html scrollbarbutton[type="decrement"] {
	margin: 0px -0px 0px -7px !important;
	min-width: 10px !important;
	min-height: 10px !important;
	background: rgba(30,30,30,1) !important;
}
html scrollbarbutton[type="decrement"]:hover {
	background: rgba(90,90,90,0.8) !important;
}
html scrollbarbutton[type="decrement"]:hover:active {
	background: rgba(130,130,130,0.8) !important;
}
html scrollbar[orient="vertical"] > scrollbarbutton[type="decrement"] {
	margin: -7px 0px 0px 0px !important;
	min-width: 10px !important;
	min-height: 10px !important;
	background: rgba(30,30,30,1) !important;
}
html scrollbar[orient="vertical"] > scrollbarbutton[type="decrement"]:hover {
	background: rgba(90,90,90,0.8) !important;
}
html scrollbar[orient="vertical"] > scrollbarbutton[type="decrement"]:hover:active {
	background: rgba(130,130,130,0.8) !important;
}

html scrollcorner {
	background: rgba(30,30,30,1) !important;
	max-width: 16px !important;
	max-height: 16px !important;
}
