css:
@-moz-document url-prefix("https://getpocket.com/a/") {
body, .side-nav, #page .pkt-nav {
    background: #fff; 
    font-weight: 300 !important }
.pkt-nav .pagenav_sectiontoggle .queue_togglesection_optionselected {
    border-color: rgb(239,64,86)}
.queue_secondarynav .queue_title {
    font-size: 2em;
}
.original_url_container, .favicon, .nav-username, #pagenav_explore, .item_type_spoc, .toolbar_queue_rainbow  {
    display: none!important }
.thumb, .pocket_logo, a.hint-item, .nav-downnotch, .nav-avatar {
	opacity: .5}    
.popover-new-bottomleft, .popover-new-list,  #page .pkt-nav .rightItem, .nav-avatar, .popover-profile-avatar, .thumb, .pocket_logo {
    -webkit-filter: grayscale(100%); 
    filter: grayscale(100%);
}
.title { 
    font-weight: 300 !important;
    font-size: 1.75em !important;
	color: #000}
a:hover, a:focus, .nav-sublist>span:hover, .queue_togglesection_optionselected, .item_link:hover+.title, .queue_togglesection_option:hover {
   color: #afafaf !important;}
.popover-new-list a:hover {
    background: #afafaf !important;
	color: #fff !important}
.queue_secondarynav .queue_toggle_listqueue {
    margin-top: .9em;}
.queue_togglesection_text {
    font-weight: 300 !important }
.button-emptyrecommend {
    background-image: none;
	border-radius: 0;
	border: none;
	text-shadow: none;}
.queue_list .topic_detail {
    border-radius: 0}
}

@-moz-document url-prefix("https://getpocket.com/a/queue/list/") {
.thumb {
    display: none!important }
.tags .tag {
    border-radius: 0 !important;
	background: #e0e0e0 !important;}
.page_queue_list .item .title {
    padding: 20px 15px !important}
.tags {
    margin: 20px 0 !important}
.page_queue_list .item .sub {
    margin-left: 8px !important} 
.page_queue_list .queue_secondarynav{    
    margin: 1.3em 3.7em 1em;
	max-width: 100%;}
a.title {
    float: left !important;
	max-width: 95%;
	text-overflow: ellipsis;}
.item .item_type_normal, .item_link .start_articleview, .item_content {
    height: 60px !important; min-height: 0 !important;}
ul.sub.clearfix {margin: 0 !important}
.item_content {
    margin: 0 3.7em;
    max-width: 100%}
.page_queue_list .queue_list .topic {
	margin-top: 20px;}
}

@-moz-document url("https://getpocket.com/a/recommended/") {
.thumb {
	margin-top: 32px;}
.format_title {
    float: left}
.item_content {
    -webkit-filter: grayscale(100%); 
    filter: grayscale(100%);}
}
