css:
@-moz-document url-prefix("http://fontawesome.io/cheatsheet/") {
/* FontAwesome Cheatsheet Booster */

.font-awesome .container .row .col-md-4 {
	font-size: 1.6rem;
	padding: 2rem 0;
}

.font-awesome .container .row .col-md-4 .fa {
	font-size: 5rem;
    float: left;
    margin: 0 1rem;
}

.font-awesome .container .row .col-md-4:hover {
	background: rgba(0, 0, 0, .85);
	color: white;
}

.font-awesome .container .row .col-md-4 small {
    display: none;
}

.font-awesome .container .fontawesome-icon-list a .fa {
    text-align: center;
}
}
