css:
@-moz-document domain("docs.google.com") {
  
/* Ugly stuff that should be hidden but it is not */
.docs-titlebar-buttons, .gb_b, .gb_db, .gb_R, .gb_9a, .docs-titlebar-badges,
	.docs-title-save-label-text, .docs-explore-widget-button,
  		#docs-extensions-menu, #docs-help-menu, #viewModeButton {
  		
          display: none;
}
  
/* Minimal toolbars */
#docs-branding-container.docs-branding-documents {
    background-color: #2b579a;
}
#docs-toolbar-wrapper {
    background-image: none;
    background-color: #ffffff;
}
#docs-toolbar-wrapper {
    border-top: none;
    border-bottom: none;
}
.goog-toolbar-separator.goog-inline-block {
    border-left: 1px solid #ffffff;
    margin: 1px 5px 0 4px;
}
#docs-branding-container, .docs-branding-documents, .docs-branding-crossfade-transition-disabled {
    height: 96px;
}
#docs-branding-logo {
    height: 88px;
}
.goog-toolbar-button-hover, .goog-toolbar-menu-button-hover, .goog-toolbar-combo-button-hover, .goog-toolbar-button-checked {
    background-color:#eee;
    cursor:pointer;
    opacity:0.55;
    background:#eee;
    border-color: transparent;
    background-image: none;   
    border-radius: 0;
    box-shadow: none;
    color: black;
	}
}
