css:
@-moz-document domain("www.terraform.io") {
body {
    background-color: #151515;
    color: #959595;
}
.highlight {
    background-color: #272727;
}
pre code,
pre.highlight code,
#inner h1,
#inner h2,
#inner h3,
#inner h4 {
    color: #909090;
}
.alert-info {
    background-color: #76a1b7;
    color: #302790;
    border-color: #80abc1;
}
#docs-sidebar ul.nav.docs-sidenav li a {
    color: #999999;
}
#docs-sidebar ul.nav.docs-sidenav li a:hover {
    color: #5596ce;
}
}
