css:
@-moz-document domain("imgur.com") {
.post {
	margin-bottom: 92px;
	border: none;
	height: 210px;
}
.post .hover {
	display: block;
	width: 160px;
	max-height: 70px;
	margin-left: 0;
	margin-top: -2px;
	border-radius: 0;
	-moz-border-radius: 0;
	-webkit-border-radius: 0;
	-webkit-box-shadow: none;
	-moz-box-shadow: none;
	box-shadow: none;
	border: none;
	padding: 0 10px 10px 10px;
	background-color: #474a51;
}
.post .hover:hover {
	max-height: none;
	min-height: 70px;
}
.post .post-info {
	background-color: #51545b!important;
	width: 155px!important;
	max-width: 155px!important;
	padding: 12.5px;
	color: #b4b9c2!important;
	border-top-color: #474a51!important;
	font-size: 11px;
	bottom: -1px;
}
.post .point-info {
	bottom: 0!important;
	background-color: #474a51!important;
	margin-bottom: -0px!important;
	height: 30px!important;
	cursor: default;
}
.post .point-info>div {
	height: 32px;
}
.post .point-info .point-info-points {
	font-size: 12px;
}
.matched-search-term {
	background-color: #8b919c;
	color: #141518;
}
.post:hover .gradient-transparent-black {
	background: #474a51!important;
}
.post p {
	height: 54px;
	margin-bottom: 16px;
}
.post img {
	outline: #474a51 solid 1px;
}
.post p:hover {
	position: relative;
	height: auto;
	background: #474a51!important;
	z-index: 999999;
	padding: 0px 10px 0 10px;
	margin-left: -10px;
	width: 160px
}
.post.green,
.post.red {
	border: none;
}
}
