css:
@-moz-document domain("live.com") {
/* For old Outlook.com */

#RightRailContainer {
    display: none;
}
.WithRightRail {
    right: 0 !important;
}
/* For new Outlook.com */

._n_h {
    display: none;
    width: 0;
}
#primaryContainer div[style*="right: 165px;"] {
    right: 0 !important;
}
/* email container */

._rp_g {
    padding-top: 15px;
    margin-left: 20px;
}
/* container for header message */

._rp_L4 {
    opacity: 0.55;
}
._rp_L4:hover {
    opacity: 1;
}
span.allowTextSelection {
    font-size: 14px;
}
/* subject header */

._rp_h,
._rp_i {
    margin: 0;
}
._rp_l2 {
    padding: 0;
}
/* cat spacing */

._rp_u2 {
    margin: 0;
}
._rp_e1 {
    float: left;
    margin-right: 4px;
}
}
