css:
@-moz-document domain(bluebirdjs.com), url-prefix(https://disqus.com/embed/comments/?base=default&f=bluebirdjs) {
  body, p, li, ul, i, div.textarea-wrapper, input.input--text {
    background-color: #111 !important;
    color: #aaa !important;
  }

  h1, h2, h3, h4, h5, h6, h1 *, h2 *, h3 *, h4 *, h5 *, h6 * {
    color: #4c81c9;
  }

  pre, code, .highlight {
    background-color: #222;
    color: #ccc;
  }
}
